import React from 'react';
import documentService from '../services/documentService';

const SimpleDocumentViewer = ({ document, onClose }) => {
  if (!document) return null;

  const handleBackdropClick = (e) => {
    if (e.target === e.currentTarget) {
      onClose();
    }
  };

  const handleDownload = async () => {
    try {
      await documentService.downloadDocument(document.id, document.file_name);
    } catch (error) {
      console.error('Erreur lors du t√©l√©chargement:', error);
      alert('Erreur lors du t√©l√©chargement du document');
    }
  };

  const getFileExtension = (filename) => {
    if (!filename) return '';
    const parts = filename.split('.');
    return parts.length > 1 ? parts[parts.length - 1].toLowerCase() : '';
  };

  const extension = getFileExtension(document.file_name);



  const renderDocumentContent = () => {
    console.log('üîç Analyse du document:', {
      fileName: document.file_name,
      extension: extension,
      documentType: document.document_type,
      fileSize: document.file_size,
      id: document.id
    });

    // V√©rifier si c'est un PDF
    const isPdf = extension === 'pdf' ||
                  document.file_name?.toLowerCase().includes('.pdf') ||
                  document.document_type === 'pdf';

    if (isPdf) {
      const token = localStorage.getItem('access_token');
      const previewUrl = `http://localhost:8000/documents/${document.id}/preview/?token=${token}`;

      console.log('üìÑ Chargement PDF:', { fileName: document.file_name, url: previewUrl });

      return (
        <iframe
          src={previewUrl}
          style={{
            width: '100%',
            height: '980px',
            border: 'none'
          }}
          title={`Aper√ßu de ${document.title}`}
        />
      );
    }

    // V√©rifier si c'est une image (plusieurs m√©thodes de d√©tection)
    const imageExtensions = ['jpg', 'jpeg', 'png', 'gif', 'bmp', 'webp', 'svg'];
    const isImage = imageExtensions.includes(extension) ||
                    document.file_name?.toLowerCase().match(/\.(jpg|jpeg|png|gif|bmp|webp|svg)$/i) ||
                    document.document_type?.includes('image') ||
                    // Si pas d'extension claire, essayer de d√©tecter par le nom de fichier
                    (extension === '' && document.file_name?.match(/^\d+_\d+/)) || // Pattern comme "468358500_878..."
                    // D√©tecter par taille de fichier (images typiques entre 10KB et 10MB)
                    (extension === '' && document.file_size && document.file_size > 10000 && document.file_size < 10000000) ||
                    // Forcer l'affichage comme image pour les fichiers sans extension
                    (extension === '' || extension.length > 4);

    if (isImage) {
      const token = localStorage.getItem('access_token');
      const imageUrl = `http://localhost:8000/documents/${document.id}/preview/?token=${token}`;

      // D√©terminer la m√©thode de d√©tection utilis√©e
      let detectionMethod = 'inconnue';
      if (imageExtensions.includes(extension)) detectionMethod = 'extension';
      else if (document.file_name?.toLowerCase().match(/\.(jpg|jpeg|png|gif|bmp|webp|svg)$/i)) detectionMethod = 'regex extension';
      else if (document.document_type?.includes('image')) detectionMethod = 'document_type';
      else if (extension === '' && document.file_name?.match(/^\d+_\d+/)) detectionMethod = 'pattern num√©rique';
      else if (extension === '' && document.file_size && document.file_size > 10000 && document.file_size < 10000000) detectionMethod = 'taille fichier';
      else if (extension === '' || extension.length > 4) detectionMethod = 'fallback sans extension';

      console.log('üñºÔ∏è Chargement image:', {
        fileName: document.file_name,
        extension: extension,
        detectionMethod: detectionMethod,
        url: imageUrl
      });

      return (
        <div style={{ textAlign: 'center', padding: '1rem' }}>
          <img
            src={imageUrl}
            alt={document.title}
            style={{
              maxWidth: '100%',
              maxHeight: '900px',
              objectFit: 'contain',
              border: '1px solid #ddd',
              borderRadius: '4px',
              boxShadow: '0 2px 8px rgba(0,0,0,0.1)'
            }}
            onLoad={() => console.log('‚úÖ Image charg√©e avec succ√®s')}
            onError={(e) => console.error('‚ùå Erreur chargement image:', e)}
          />
        </div>
      );
    }

    // V√©rifier si c'est un fichier Word
    const wordExtensions = ['doc', 'docx'];
    const isWord = wordExtensions.includes(extension) ||
                   document.file_name?.toLowerCase().match(/\.(doc|docx)$/i) ||
                   document.document_type?.includes('word') ||
                   document.document_type?.includes('msword') ||
                   document.document_type?.includes('wordprocessingml');

    if (isWord) {
      const token = localStorage.getItem('access_token');
      const wordUrl = `http://localhost:8000/documents/${document.id}/preview/?token=${token}`;

      console.log('üìù Chargement document Word:', {
        fileName: document.file_name,
        extension: extension,
        url: wordUrl
      });

      return (
        <div style={{ textAlign: 'center', padding: '1rem' }}>
          <div style={{ marginBottom: '1rem', color: '#28a745' }}>
            <i className="fas fa-file-word" style={{ fontSize: '2rem', marginRight: '0.5rem' }}></i>
            <strong>Document Word d√©tect√©</strong>
          </div>
          <iframe
            src={wordUrl}
            style={{
              width: '100%',
              height: '900px',
              border: '1px solid #ddd',
              borderRadius: '4px',
              boxShadow: '0 2px 8px rgba(0,0,0,0.1)'
            }}
            title={`Aper√ßu de ${document.title}`}
            onLoad={() => console.log('‚úÖ Document Word charg√© avec succ√®s')}
            onError={(e) => console.error('‚ùå Erreur chargement Word:', e)}
          />
          <div style={{ marginTop: '1rem', fontSize: '0.9rem', color: '#6c757d' }}>
            <p>üí° Si le document ne s'affiche pas correctement, cliquez sur "T√©l√©charger" pour l'ouvrir avec Microsoft Word.</p>
          </div>
        </div>
      );
    }

    // V√©rifier si c'est un fichier texte
    const textExtensions = ['txt', 'md', 'rtf', 'csv'];
    const isText = textExtensions.includes(extension) ||
                   document.file_name?.toLowerCase().match(/\.(txt|md|rtf|csv)$/i) ||
                   document.document_type?.includes('text') ||
                   document.document_type?.includes('plain');

    if (isText) {
      const token = localStorage.getItem('access_token');
      const textUrl = `http://localhost:8000/documents/${document.id}/preview/?token=${token}`;

      console.log('üìÑ Chargement fichier texte:', {
        fileName: document.file_name,
        extension: extension,
        url: textUrl
      });

      return (
        <div style={{ textAlign: 'center', padding: '1rem' }}>
          <div style={{ marginBottom: '1rem', color: '#17a2b8' }}>
            <i className="fas fa-file-alt" style={{ fontSize: '2rem', marginRight: '0.5rem' }}></i>
            <strong>Fichier texte d√©tect√©</strong>
          </div>
          <iframe
            src={textUrl}
            style={{
              width: '100%',
              height: '900px',
              border: '1px solid #ddd',
              borderRadius: '4px',
              boxShadow: '0 2px 8px rgba(0,0,0,0.1)',
              backgroundColor: '#f8f9fa'
            }}
            title={`Aper√ßu de ${document.title}`}
            onLoad={() => console.log('‚úÖ Fichier texte charg√© avec succ√®s')}
            onError={(e) => console.error('‚ùå Erreur chargement texte:', e)}
          />
        </div>
      );
    }

    // Fallback : essayer d'afficher comme image si pas d'extension claire
    if (!extension || extension === '') {
      const token = localStorage.getItem('access_token');
      const fallbackUrl = `http://localhost:8000/documents/${document.id}/preview/?token=${token}`;

      console.log('üîÑ Tentative fallback image pour:', {
        fileName: document.file_name,
        url: fallbackUrl
      });

      return (
        <div style={{ textAlign: 'center', padding: '1rem' }}>
          <div style={{ marginBottom: '1rem', color: '#6c757d' }}>
            <small>Tentative d'affichage comme image...</small>
          </div>
          <img
            src={fallbackUrl}
            alt={document.title}
            style={{
              maxWidth: '100%',
              maxHeight: '900px',
              objectFit: 'contain',
              border: '1px solid #ddd',
              borderRadius: '4px',
              boxShadow: '0 2px 8px rgba(0,0,0,0.1)'
            }}
            onLoad={() => {
              console.log('‚úÖ Fallback image charg√©e avec succ√®s');
            }}
            onError={(e) => {
              console.error('‚ùå Fallback image √©chou√©:', e);
              // Afficher le message d'erreur
              e.target.style.display = 'none';
              e.target.nextSibling.style.display = 'block';
            }}
          />
          <div style={{
            display: 'none',
            textAlign: 'center',
            padding: '2rem',
            color: '#6c757d'
          }}>
            <div style={{ fontSize: '3rem', marginBottom: '1rem' }}>üìÑ</div>
            <h5>Aper√ßu non disponible</h5>
            <p>Ce fichier ne peut pas √™tre pr√©visualis√©.</p>
            <p>Cliquez sur "T√©l√©charger" pour ouvrir le fichier.</p>
          </div>
        </div>
      );
    }

    // Autres types de fichiers
    return (
      <div style={{
        textAlign: 'center',
        padding: '3rem',
        color: '#6c757d'
      }}>
        <div style={{ fontSize: '3rem', marginBottom: '1rem' }}>üìÑ</div>
        <h5>Aper√ßu non disponible</h5>
        <p>Ce type de fichier (.{extension || 'inconnu'}) ne peut pas √™tre pr√©visualis√©.</p>
        <p>Cliquez sur "T√©l√©charger" pour ouvrir le fichier.</p>
      </div>
    );
  };

  return (
    <div 
      style={{
        position: 'fixed',
        top: 0,
        left: 0,
        width: '100%',
        height: '100%',
        backgroundColor: 'rgba(0, 0, 0, 0.7)',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        zIndex: 9999,
        padding: '20px'
      }}
      onClick={handleBackdropClick}
    >
      <div
        style={{
          backgroundColor: 'white',
          borderRadius: '8px',
          maxWidth: '95%',
          maxHeight: '95%',
          width: '1800px',
          height: '1200px',
          display: 'flex',
          flexDirection: 'column',
          overflow: 'hidden',
          boxShadow: '0 10px 30px rgba(0, 0, 0, 0.3)'
        }}
        onClick={(e) => e.stopPropagation()}
      >
        {/* Header */}
        <div style={{
          padding: '1rem 1.5rem',
          borderBottom: '1px solid #dee2e6',
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center',
          backgroundColor: '#f8f9fa'
        }}>
          <h5 style={{ margin: 0, color: '#495057' }}>
            üìÑ {document.title}
          </h5>
          <button
            onClick={onClose}
            style={{
              background: 'none',
              border: 'none',
              fontSize: '1.5rem',
              cursor: 'pointer',
              color: '#6c757d',
              padding: '0',
              width: '30px',
              height: '30px'
            }}
          >
            √ó
          </button>
        </div>

        {/* Body */}
        <div style={{
          padding: '1.5rem',
          flexGrow: 1,
          overflow: 'auto'
        }}>
          {/* Info */}
          <div style={{
            marginBottom: '1rem',
            padding: '1rem',
            backgroundColor: '#f8f9fa',
            borderRadius: '6px',
            borderLeft: '4px solid #007bff'
          }}>
            {document.description && (
              <div><strong>Description :</strong> {document.description}</div>
            )}
          </div>

          {/* Content */}
          <div style={{
            border: '1px solid #dee2e6',
            borderRadius: '6px',
            overflow: 'hidden',
            minHeight: '1000px',
            height: '1000px',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center'
          }}>
            {renderDocumentContent()}
          </div>
        </div>

        {/* Footer */}
        <div style={{
          padding: '1rem 1.5rem',
          borderTop: '1px solid #dee2e6',
          backgroundColor: '#f8f9fa',
          display: 'flex',
          justifyContent: 'flex-end',
          gap: '0.5rem'
        }}>
          <button
            className="btn btn-outline-success"
            onClick={handleDownload}
          >
            üì• T√©l√©charger
          </button>
          <button
            className="btn btn-secondary"
            onClick={onClose}
          >
            Fermer
          </button>
        </div>
      </div>
    </div>
  );
};

