import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import authService from '../services/authService';
import documentService from '../services/documentService';
import ModernDocumentList from '../components/ModernDocumentList';
import DocumentUpload from '../components/DocumentUpload';
import SearchAndFilter from '../components/SearchAndFilter';


import SkeletonLoader from '../components/SkeletonLoader';
import Pagination from '../components/Pagination';
import HeroSection from '../components/HeroSection';
import ModernNavbar from '../components/ModernNavbar';
import FloatingActionButton from '../components/FloatingActionButton';

import SimpleDocumentViewer from '../components/SimpleDocumentViewer';

import usePagination from '../hooks/usePagination';
import { useApp } from '../contexts/AppContext';
import { getUserPermissions } from '../utils/permissions';


const Dashboard = () => {
  const [showUpload, setShowUpload] = useState(false);
  const [showAdvancedStats, setShowAdvancedStats] = useState(false);
  const [viewingDocument, setViewingDocument] = useState(null);
  const [notification, setNotification] = useState(null);
  const navigate = useNavigate();
  const { state, actions } = useApp();

  // Ajouter les styles CSS pour l'animation
  React.useEffect(() => {
    const style = document.createElement('style');
    style.textContent = `
      @keyframes slideInRight {
        from {
          transform: translateX(100%);
          opacity: 0;
        }
        to {
          transform: translateX(0);
          opacity: 1;
        }
      }
    `;
    document.head.appendChild(style);

    return () => {
      document.head.removeChild(style);
    };
  }, []);

  // Obtenir les permissions de l'utilisateur
  const userPermissions = getUserPermissions(state.user);

  // Fonction pour afficher une notification temporaire
  const showNotification = (message, type = 'info') => {
    setNotification({ message, type });
    setTimeout(() => setNotification(null), 3000); // Dispara√Æt apr√®s 3 secondes
  };

  // Get filtered documents
  const filteredDocuments = actions.getFilteredDocuments();

  // Pagination
  const {
    currentPage,
    totalPages,
    itemsPerPage,
    paginatedData,
    totalItems,
    goToPage,
    changeItemsPerPage
  } = usePagination(filteredDocuments, 6);

  useEffect(() => {
    // V√©rifier l'authentification
    if (!authService.isAuthenticated()) {
      navigate('/login');
      return;
    }

    loadUserData();
    loadDocuments();
  }, [navigate]);

  // Log de debug pour voir combien de documents sont re√ßus
  useEffect(() => {
    console.log('üìä Documents Dashboard:', {
      total: state.documents.length,
      filtered: filteredDocuments.length,
      paginated: paginatedData.length,
      currentPage: currentPage,
      totalPages: totalPages,
      itemsPerPage: itemsPerPage
    });
  }, [state.documents, filteredDocuments, paginatedData, currentPage, totalPages, itemsPerPage]);

  const loadUserData = async () => {
    try {
      actions.setLoading(true);
      const userData = await authService.getProfile();
      actions.setUser(userData);
    } catch (err) {
      console.error('Erreur lors du chargement du profil:', err);
      actions.showNotification('error', 'Erreur lors du chargement du profil');
    } finally {
      actions.setLoading(false);
    }
  };

  const loadDocuments = async () => {
    try {
      console.log('üîÑ D√©but du chargement des documents...');
      actions.setLoading(true);
      const documentsData = await documentService.getDocuments();
      console.log('üìä Donn√©es re√ßues du serveur:', documentsData);
      const documents = documentsData.results || documentsData;
      console.log('üìã Documents √† d√©finir:', documents);
      actions.setDocuments(documents);
      console.log('‚úÖ Documents d√©finis dans le contexte');
    } catch (err) {
      console.error('‚ùå Erreur lors du chargement des documents:', err);
      actions.showNotification('error', 'Erreur lors du chargement des documents');
    } finally {
      actions.setLoading(false);
    }
  };

  const handleLogout = () => {
    authService.logout();
  };

  const handleUploadSuccess = async (document) => {
    console.log('üìÑ Document ajout√© avec succ√®s:', document);
    setShowUpload(false);
    actions.showNotification('success', 'Document ajout√© avec succ√®s !');

    // Solution √©l√©gante : redirection vers la m√™me page pour forcer le rechargement
    console.log('üîÑ Redirection vers dashboard pour synchronisation...');
    navigate('/dashboard', { replace: true });

    // Forcer le rechargement des donn√©es apr√®s la navigation
    setTimeout(() => {
      loadDocuments();
    }, 100);
  };

  const openDocumentInNewTab = async (document) => {
    const token = localStorage.getItem('access_token');
    const previewUrl = `http://localhost:8000/documents/${document.id}/preview/?token=${token}`;

    console.log(`üîó Ouverture dans nouvel onglet - Type: ${document.document_type}, URL: ${previewUrl}`);

    // M√©thode la plus fiable : cr√©er un lien et simuler un clic
    const openWithLink = () => {
      const link = document.createElement('a');
      link.href = previewUrl;
      link.target = '_blank';
      link.rel = 'noopener noreferrer';
      link.style.display = 'none';

      // Ajouter au DOM temporairement
      document.body.appendChild(link);

      // Simuler un clic utilisateur (plus fiable que window.open)
      const clickEvent = new MouseEvent('click', {
        bubbles: true,
        cancelable: true,
        view: window,
        ctrlKey: false,
        metaKey: false
      });

      link.dispatchEvent(clickEvent);

      // Nettoyer
      setTimeout(() => {
        if (document.body.contains(link)) {
          document.body.removeChild(link);
        }
      }, 100);

      console.log('‚úÖ Lien temporaire cr√©√© et cliqu√©');
    };

    try {
      // Essayer d'abord window.open (plus rapide si √ßa marche)
      const newWindow = window.open('', '_blank', 'noopener,noreferrer,width=1200,height=800');

      if (newWindow && !newWindow.closed) {
        // Si la fen√™tre s'ouvre, naviguer vers l'URL
        newWindow.location.href = previewUrl;
        console.log('‚úÖ Nouvel onglet ouvert avec window.open');
      } else {
        // Fallback avec lien temporaire
        console.warn('‚ö†Ô∏è window.open bloqu√©, utilisation du fallback');
        openWithLink();
      }
    } catch (error) {
      console.error('‚ùå Erreur avec window.open:', error);
      // Fallback avec lien temporaire
      openWithLink();
    }
  };

  const handleViewDocument = (document) => {
    // Pour les documents de type "fiche" et "autre", ouvrir dans un nouvel onglet
    if (document.document_type === 'fiche' || document.document_type === 'other') {
      showNotification(`Ouverture de "${document.title}" dans un nouvel onglet...`, 'info');
      openDocumentInNewTab(document);
      return;
    }

    // Pour les autres types (PDF, images), utiliser le visualiseur modal
    console.log(`üìã Ouverture dans visualiseur modal - Type: ${document.document_type}`);
    setViewingDocument(document);
  };

  const handleCloseViewer = () => {
    setViewingDocument(null);
  };

  const handleDeleteDocument = async (documentId) => {
    try {
      await documentService.deleteDocument(documentId);
      actions.removeDocument(documentId);
      // Recharger les documents pour s'assurer de la synchronisation
      await loadDocuments();
      actions.showNotification('success', 'Document supprim√© avec succ√®s !');
    } catch (err) {
      console.error('Erreur lors de la suppression:', err);
      actions.showNotification('error', 'Erreur lors de la suppression du document');
      throw err;
    }
  };

  const handleUpdateDocument = async () => {
    // Recharger les documents apr√®s une modification
    await loadDocuments();
  };

  if (state.loading && !state.user) {
    return (
      <div className="d-flex justify-content-center align-items-center min-vh-100">
        <div className="spinner-border text-primary" role="status">
          <span className="visually-hidden">Chargement...</span>
        </div>
      </div>
    );
  }



  return (
    <div className="min-vh-100">
      {/* Modern Navbar */}
      <ModernNavbar />

      {/* Hero Section */}
      <HeroSection user={state.user} />

      {/* Main Content */}
      <div className="container py-4">




        {/* Search and Filter */}
        <SearchAndFilter />



        {/* Actions */}
        <div className="row mb-4">
          <div className="col-12">
            <div className="d-flex justify-content-between align-items-center">
              <h2 className="enhanced-title">
                <i className="fas fa-book-open me-2 text-primary"></i>
                Biblioth√®que Partag√©e
                <span className="badge bg-primary ms-2 enhanced-badge">{totalItems}</span>
                {state.documents.length !== totalItems && (
                  <small className="text-muted ms-2">
                    (sur {state.documents.length} total)
                  </small>
                )}
              </h2>

              {userPermissions.canAdd && (
                <button
                  className="btn btn-success enhanced-btn"
                  onClick={() => setShowUpload(true)}
                >
                  <i className="fas fa-plus me-2"></i>
                  Ajouter un document
                </button>
              )}
            </div>
          </div>
        </div>

        {/* Upload Modal - Seulement pour les admins */}
        {showUpload && userPermissions.canAdd && (
          <DocumentUpload
            onClose={() => setShowUpload(false)}
            onSuccess={handleUploadSuccess}
          />
        )}

        {/* Documents List */}
        <div className="row">
          <div className="col-12">
            {state.loading ? (
              <SkeletonLoader count={6} />
            ) : (
              <>
                <ModernDocumentList
                  documents={paginatedData}
                  onDelete={handleDeleteDocument}
                  onUpdate={handleUpdateDocument}
                  onView={handleViewDocument}
                />

                {/* Pagination */}
                {totalItems > 6 && (
                  <Pagination
                    currentPage={currentPage}
                    totalPages={totalPages}
                    totalItems={totalItems}
                    itemsPerPage={itemsPerPage}
                    onPageChange={goToPage}
                    onItemsPerPageChange={changeItemsPerPage}
                  />
                )}
              </>
            )}
          </div>
        </div>



        {/* Floating Action Button - Seulement pour les admins */}
        {userPermissions.canAdd && (
          <FloatingActionButton
            onClick={() => setShowUpload(true)}
            icon="fas fa-plus"
            tooltip="Ajouter un document"
          />
        )}

        {/* Visualiseur de documents */}
        {viewingDocument && (
          <SimpleDocumentViewer
            document={viewingDocument}
            onClose={handleCloseViewer}
          />
        )}

        {/* Notification temporaire */}
        {notification && (
          <div
            style={{
              position: 'fixed',
              top: '20px',
              right: '20px',
              zIndex: 9999,
              maxWidth: '400px',
              padding: '1rem 1.5rem',
              borderRadius: '8px',
              boxShadow: '0 4px 12px rgba(0, 0, 0, 0.15)',
              backgroundColor: notification.type === 'info' ? '#d1ecf1' : '#f8d7da',
              border: `1px solid ${notification.type === 'info' ? '#bee5eb' : '#f5c6cb'}`,
              color: notification.type === 'info' ? '#0c5460' : '#721c24',
              animation: 'slideInRight 0.3s ease-out'
            }}
          >
            <div style={{ display: 'flex', alignItems: 'center', gap: '0.5rem' }}>
              <i className={`fas ${notification.type === 'info' ? 'fa-info-circle' : 'fa-exclamation-triangle'}`}></i>
              <span>{notification.message}</span>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
